import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, Link } from 'react-router-dom';
import './Dashboard.css'
import Axios from 'axios'
import Content from './Content/Content'
import Profile from './UserProfile/User'
import Home from './Home/Home'
import axios from "../../axios";

export default class Dashboard extends Component {

  constructor(props) {
    super(props)

    let loginStatus = false
    const token = localStorage.getItem("token");
    if(token) loginStatus = true

    this.state = {
      Users: [],
      loginStatus ,

    }
    this.handleLogOut = this.handleLogOut.bind(this);
    Axios.defaults.withCredentials = true
  }

  componentDidMount(){


        axios.get(`/getUserId`)
        .then( response =>{
            console.log(response);

            this.setState({Users: response.data})


        }).catch(error =>{
            console.log(error);
        })

    }




  handleLogOut(){

    this.setState({
      loginStatus : false
    })

  }

  render(){

    const {Users} = this.state;


    if(this.state.loginStatus === false){
      return (<Redirect to ="/logout"/> );
    }

    return (
      <>
        {/* {this.state.LoggedOut ? (<Redirect to="/login"/>): ( */}
          <div className="dashboard">
          <input type="checkbox" id="check"/>
            {/* header area start */}
            <header>
              <label for="check">
                <i class="fas fa-bars" id="sidebar_btn"></i>
              </label>
              <div class="left_area">
                <h3>You <span>Directory</span></h3>
              </div>
              <div class="right_area">
                <button onClick={this.handleLogOut} class="logout_btn">Logout</button>
              </div>
            </header>
            {/* header area end */}
            {/* sidebar start */}
            <Router>
            <div class="sidebar">

              <div class="dashboard-profile">
                <img src={process.env.PUBLIC_URL + './img/avatar.jpg'} class="profile_image" alt=""/>
                {  Users.length ?
                  Users.map(user => 
                  <h4 key={user.userId}>{user.username}</h4>
                  ) : null
              }
              </div>
              <hr/>
              <Link to="/user_dashboard/Home"><i class="fas fa-desktop"></i><span>Dashboard</span></Link>
              <Link to="/user_dashboard/profile"><i class="fal fa-user"></i><span>Profile</span></Link>
              <Link to="/user_dashboard/content"><i class="fas fa-th"></i><span>Contents</span></Link>

            </div>
            {/* sidebar end */}
            <div className="content">



              <Switch>
                <Route path="/user_dashboard/Home" component={Home}></Route>
                <Route path="/user_dashboard/content" component={Content}></Route>
                <Route path="/user_dashboard/profile" component={Profile}></Route>

            </Switch>
            </div>
          </Router>
            {/* <Switch>
              <div class="content">

                <Route path="#addDetails" component={addContent}/>


              </div>
            </Switch> */}
        </div>
        {/* )} */}
      </>
    );
  }
}
