    render() {

        if (!this.props.show) {
            return null;
        }
        return (
            <div className={ this.props.alertHide }>
                <div className="modal-wrapper" >
                    <div className="modal-header">
                        <div className="success-icon">
                            <i class="fas fa-exclamation"></i>
                        </div>
