                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-3-col">
                                { this.props.children }
                            </div>
                        </div>
                        {/* <div className="modal-footer">
                            <button type="button" onClick={ this.props.handleAddDet } className="btn-footer">Add</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div> */}
                    </div>
                </div>
            </div>
        );

    }
}
