import React, { Component } from 'react'
import './User.css'
import axios from "../../../axios";
import Modal from 'react-bootstrap/Modal'

export default class User extends Component {

    constructor(props) {
        super(props)

        this.state = {
            Users: [],
            address: '',
            Msg: false
        }
        this.closeMsg = this.closeMsg.bind(this);
        this.handleChange = this.handleChange.bind(this);
        // this.handleUpdate = this.handleUpdate.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    componentDidMount(){
        axios.get(`/getUserId`)
        .then( response =>{
            // console.log(response);

            this.setState({Users: response.data})


        }).catch(error =>{
            console.log(error);
        })

    }

    handleUpdate(e) {
        e.preventDefault();
        console.log(this.state.address);
        // console.log(id);
        axios.put(`/updateUserId`, this.state)
            .then(({data}) => {
                // console.log('Update user RESPONSE', data);
                this.setState({
                    Msg: true,
                })

            })

            .catch(error => {
                console.log(error);

            })
    }

    closeMsg() {
        this.setState({
            Msg: false,
        })
    }

    render() {
        const {Users, Msg} = this.state;

        return (
            <div>
                <div className="dash-users">
                    <h3>Profile <span>Page</span></h3>
                    { Users.length ?
                        Users.map(user =>

                    <div className="div-card-row row" key={ user.userId }>


                        <div className="col-sm-6" >
                            <div className="div-row">
                                    <label><i class="far fa-user"></i>&nbsp;&nbsp;Name</label> &nbsp; &nbsp;
                                <p>{ user.username }</p>
                            </div>
                            <div className="div-row">
                                    <label><i class="far fa-envelope"></i>&nbsp;&nbsp;Email</label> &nbsp; &nbsp;
                                <p>{ user.email }</p>
                            </div>
                            <div className="div-row">
                                    <label><i class="fal fa-phone-alt"></i>&nbsp;&nbsp;Phone number</label> &nbsp; &nbsp;
                                <p>{ user.phonenumber }</p>
                            </div>
                            <div className="div-row">
                                    <label><i class="fal fa-map-marker-alt"></i>&nbsp;&nbsp;Address</label> &nbsp; &nbsp;
                                <p>{user.address}</p>
                            </div>
                        </div>

                        <div className="col-sm-6">
                            <form >
                                <h5>Update Profile</h5><br />
                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Address</label>
                                    </div>
                                    <div className="div-textarea">
                                        <textarea rows='2' class="form-control" type="text" name="address" onChange={ this.handleChange } value={ this.address } required />
                                    </div>

                                </div>
                                <button onClick={ this.handleUpdate.bind(this) } class="submit-btn">Submit</button>
                            </form>
                            <Modal
                                show={ Msg }
                                onHide={ this.closeMsg }
                                centered
                            >
                                <Modal.Header closeButton>

                                </Modal.Header>
                                <Modal.Body>
                                    <p>Updated Successfully</p>
                                </Modal.Body>

                            </Modal>
                        </div>


                    </div>
                        ) : null
                    }
                </div>
            </div>
        )
    }
}
