import React, { Component } from 'react'
import '../../Admin-css/Admin-Detail.css'
import Edit from './EditBlog'
import Message from './Message'
import Alert from './AlertMessage'

import Axios from '../../../../axios'

export default class ViewBlog extends Component {

    constructor (props) {
        super(props)

        let setImage = 0

        this.state = {

            blogpost: [],
            bcategories: [],
            id: '',

            show: false,
            message: false,
            editMode: false,

            responseData: '',

            post_name: '',
            post_category: '',
            post_subcategory: '',
            post_image: null,
            post_description: '',
            post_date: '',
            post_authname: '',

            currentDate: new Date().toLocaleString(),

            formErrors: {},

            setImage,
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);

        this.closeModal = this.closeModal.bind(this);
        this.openModal = this.openModal.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.closeAlertMessage = this.closeAlertMessage.bind(this);

        this.onUpload = this.onUpload.bind(this);
        this.triggerInputFile = this.triggerInputFile.bind(this);
        this.refreshPage = this.refreshPage.bind(this);

        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    componentDidMount() {

        Axios.get('/getPost')
            .then(response => {
                // console.log(response)
                this.setState({ blogpost: response.data })
            })
            .catch(error => {
                console.log(error)
            })

        Axios.get('/getPostCategory')
            .then(response => {
                // console.log(response)
                this.setState({ bcategories: response.data })
            })
            .catch(error => {
                console.log(error)
            })
    }


    openModal = (e) => {
        this.setState({
            show: true
        })
    }

    closeModal = (e) => {
        this.setState({
            show: false
        })
    }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }

    openAlertMessage = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }

    onUpload(e) {
        // let file = e.target.files[0];
        this.setState({
            post_image: e.target.files[0],
            setImage: 1,
        })
        console.log(e.target.files[0]);
    }



    triggerInputFile() { this.fileInput.click() }




    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { post_name, post_category, post_subcategory, post_description, post_authname } = this.state

        if (post_name === '') {
            formErrors.post_nameError = "Blog Title is Required!!";
            isValid = false;
        }
        if (post_category === '') {
            formErrors.post_categoryError = "Blog Category Name is Required!!";
            isValid = false;
        }
        if (post_subcategory === '') {
            formErrors.post_subcategoryError = "Sub Category Name is Required!!";
            isValid = false;
        }
        if (post_description === '') {
            formErrors.post_descriptionError = "Description is Required!!";
            isValid = false;
        }
        if (post_authname === '') {
            formErrors.post_authnameError = "Name is Required!!";
            isValid = false;
        }


        if (post_category.length === 1) {
            formErrors.post_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_category.length === 2) {
            formErrors.post_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_category.length === 3) {
            formErrors.post_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_category.length > 50) {
            formErrors.post_categoryError = "Category Name must be of length 50";
            isValid = false;
        }
        if (post_category.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_categoryError = "No Special Characters";
            isValid = false;
        }

        if (post_subcategory.length === 1) {
            formErrors.post_subcategoryError = "Sub Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length === 2) {
            formErrors.post_subcategoryError = "Sub Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length === 3) {
            formErrors.post_subcategoryError = "Sub Category Name must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length > 50) {
            formErrors.post_subcategoryError = "Sub Category Name must be of length 50";
            isValid = false;
        }
        if (post_subcategory.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_subcategoryError = "No Special Characters";
            isValid = false;
        }

        if (post_name.length === 1) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length === 2) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length === 3) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length > 250) {
            formErrors.post_nameError = "Title must be of length 250";
            isValid = false;
        }
        if (post_name.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_nameError = "No Special Characters";
            isValid = false;
        }

        if (post_description.length > 2000) {
            formErrors.post_descriptionError = "Description must be of length 2000";
            isValid = false;
        }
        if (post_description.length < 10) {
            formErrors.post_descriptionError = "Description must be of 10 or more";
            isValid = false;
        }

        if (post_authname.length === 1) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length === 2) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length === 3) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length > 30) {
            formErrors.post_authnameError = "Name must be of length 30";
            isValid = false;
        }
        if (post_authname.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_authnameError = "No Special Characters";
            isValid = false;
        }

        this.setState({ formErrors });
        return isValid;
    }





    deletePost(id) {

        // console.log(this.state);
        Axios.delete(`/deletePost/${ id }`)
            .then(response => {

                this.setState({
                    alert: false,
                    responseData: response.data.message
                })

                this.openMessage();
            })
            .catch(error => {
                console.log(error);

            })
    }

    editPost = async (id) => {


        let editingItem = this.state.blogpost.find(post => {
            return post.post_id === id;
        })

        console.log(editingItem)

        await this.setState({
            post_name: editingItem.post_name,
            post_category: editingItem.post_category,
            post_subcategory: editingItem.post_subcategory,
            post_image: editingItem.post_image.split("/").pop(),
            post_description: editingItem.post_description,
            post_date: editingItem.post_date,
            post_authname: editingItem.post_authname,
            editMode: id,

        })

        this.openModal();

    }


    handleUpdate(e) {
        e.preventDefault();

        const isValid = this.handleFormValidation();
        if (isValid) {

            let id = this.state.editMode;

            this.setState({
                editMode: true,
            })


            if (this.state.setImage === 1) {

                let data = new FormData();
                data.append('post_name', this.state.post_name);
                data.append('post_category', this.state.post_category);
                data.append('post_subcategory', this.state.post_subcategory);
                data.append('post_image', this.state.post_image);
                data.append('post_description', this.state.post_description);
                data.append('post_date', this.state.post_date);
                data.append('post_authname', this.state.post_authname);

                Axios.put(`/editPost/${ id }/${ this.state.setImage}`, data)
                    .then(response => {

                        this.closeModal();

                        this.setState({
                            responseData: response.data.message
                        })

                        this.openMessage();
                    })

                    .catch(error => {
                        console.log(error);

                    })
            }
            else if (this.state.setImage === 0) {

                const data = {
                    post_name: this.state.post_name,
                    post_category: this.state.post_category,
                    post_subcategory: this.state.post_subcategory,
                    post_description: this.state.post_description,
                    post_date: this.state.post_date,
                    post_authname: this.state.post_authname,

                }
                Axios.put(`/editPost/${ id }`, data)
                    .then(response => {

                        this.closeModal();

                        this.setState({
                            responseData: response.data.message
                        })

                        this.openMessage();
                    })

                    .catch(error => {
                        console.log(error);

                    })
            }
        }
    }




    render() {

        const { formErrors, blogpost, bcategories, responseData } = this.state

        const showHide = this.state.show ? "edit-form display-block" : "edit-form display-none";
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        return (
            <>
                <div className="class-table">
                    <div className="section-title">
                        <h5>Blog List</h5>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>Sl. No</th>
                                <th>Image</th>
                                <th>Post Name</th>
                                <th>Category</th>
                                <th>Sub Category</th>
                                <th>Date</th>
                                <th>Action</th>

                            </tr>
                        </thead>
                        <tbody>

                            {
                                blogpost.length ?
                                    blogpost.map(post => <tr key={ post.id }>
                                        <td>{ post.post_id }</td>
                                        <td><img src={ post.post_image.replace('/public', '') } alt="" /></td>
                                        <td>{ post.post_name}</td>

                                        <td>{ post.post_category }</td>
                                        <td>{ post.post_subcategory }</td>
                                        <td>{ post.post_date }</td>

                                        <td>
                                            <div class="action-btns">
                                                <button class="div-action-btn" onClick={ this.editPost.bind(this, post.post_id) }><i class="fas fa-pen"></i></button>
                                                <button class="div-action-btn" onClick={ this.openAlertMessage.bind(this, post.post_id) } ><i class="fas fa-trash"></i></button>
                                            </div>
                                        </td>
                                    </tr>) : null
                            }

                        </tbody>
                    </table>
                    <Edit show={ this.state.show } handleClose={ this.closeModal } handleSubmit={ this.handleUpdate } showHide={ showHide }>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Post Title</label>
                            </div>
                            <div className="div-input">
                                <input class="form-control" type="text" name="post_name" onChange={ this.handleChange } value={ this.state.post_name } />
                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Post Category</label>
                            </div>
                            <div className="div-input">
                                <div className="div-select">
                                    <select class="form-control" onChange={ this.handleChange } name="post_category" value={ this.state.post_category }>
                                        <option>Select Category</option>{
                                            bcategories.length ?
                                                bcategories.map(category =>

                                                    <option name={ category.post_category } value={ category.post_category } key={ category.id }>{ category.post_category }</option>
                                                ) : null
                                        }
                                    </select>
                                </div>
                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Post Sub Category</label>
                            </div>
                            <div className="div-input">
                                <input class="form-control" type="text" name="post_subcategory" onChange={ this.handleChange } value={ this.state.post_subcategory } />
                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Image</label>
                            </div>
                            <div className="div-input">
                                <input class="form-control" value={ this.state.post_image } onClick={ this.triggerInputFile } />
                                <input ref={ fileInput => this.fileInput = fileInput } Style={ "display : none" } type="file" onChange={ this.onUpload } name="post_image" accept=".png, .jpg" />

                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Post Content</label>
                            </div>
                            <div className="div-input">
                                <textarea rows="5" class="form-control" type="text" name="post_description" onChange={ this.handleChange } value={ this.state.post_description } />
                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Date</label>
                            </div>
                            <div className="div-input">
                                <input class="form-control" type="text" onChange={ this.handleChange } name="post_date" value={ this.state.currentDate } />
                            </div>
                        </div>
                        <div className="form-box">
                            <div className="div-label">
                                <label>Your Name</label>
                            </div>
                            <div className="div-input">
                                <input class="form-control" type="text" name="post_authname" onChange={ this.handleChange } value={ this.state.post_authname } />
                            </div>
                        </div>

                    </Edit>

                    <Message show={ this.state.message } messageHide={ messageHide }>
                        <h3>Success !</h3>
                        <p>{ responseData }</p>
                        <div className="m-footer">
                            <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                        </div>
                    </Message>

                    <Alert show={ this.state.alert } alertHide={ alertHide }>
                        <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                        <div className="m-footer">

                            <button type="button" onClick={ this.deletePost.bind(this, this.state.id) } className="btn-footer">Ok</button>
                            <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                        </div>
                    </Alert>
                </div>

            </>
        )
    }
}
