import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Switch, Redirect, Link } from 'react-router-dom';
import './sidenav.css'
import axios from "../../axios";

import Home from './Home/Home'

import shopContent from './Shop/Content/Content'
import shopCategory from './Shop/Category/Category'

import blogContent from './Blog/Content/Blog'
import blogCategory from './Blog/Category/Category'

import newsContent from './News/Content/News'
import newsCategory from './News/Category/Category'


import Users from './Users/Users'
import Feedback from './Feedback/Feedback'
import Contact from './Contact/Contact'

export default class Dashboard extends Component {

  constructor(props) {
    super(props)

    let loginStatus = false
    const token = localStorage.getItem("token");
    if(token) loginStatus = true

    this.state = {
      AdminData: [],
      userCount: [],
      loginStatus ,
    }

    this.handleLogOut = this.handleLogOut.bind(this);
    axios.defaults.withCredentials = true
  }

  componentDidMount(){
    axios.get(`/getUserId`)

    .then( response =>{
      console.log(response);

      this.setState({
        AdminData: response.data
      })

    })
    .catch(error =>{
      console.log(error);
    })

  }

  handleLogOut(){

    this.setState({
      loginStatus : false
    })

  }

  render(){  

    const {AdminData} = this.state;

    if(this.state.loginStatus === false){
      return (<Redirect to ="/adlogout"/> );
    }

    return (
      <>
        <div className="side-nav">
          <input type="checkbox" id="check"/>
            {/* header area start */}
            <header>
              <div className="div-wrapper">
                <label for="check">
                  <i class="fas fa-bars" id="sidebar_btn"></i>
                </label>

              </div>
              <div class="right_area">

                {/* <button onClick={this.handleLogOut} class="logout_btn">Logout</button> */}
              </div>
            </header>
            {/* header area end */}
            {/* sidebar start */}
            <Router>
            <div class="sidebar">
              <div class="left_area">
                <h3><Link to="/admin_dashboard/Home">You Directory</Link></h3>
              </div>
              {/* <div class="dashboard-profile"> */}
                {/* <img src={process.env.PUBLIC_URL + '../img/images.png'} class="profile_image" alt=""/> */}
                {/* {  AdminData.length ?
                  AdminData.map(Admin => 
                  <h4 key={Admin.userId}>{Admin.username}</h4>
                  ) : null
              }
              </div> */}
              {/* <span class="hr"></span> */}

              {/* <Link to="/admin_dashboard/Home"><i class="fal fa-desktop"></i><span>Dashboard</span></Link> */}
              <span class="span">Shop</span>
              <Link to="/admin_dashboard/shop-category"><i class="fal fa-cogs"></i><span>Category</span></Link>
              <Link to="/admin_dashboard/shop-content"><i class="fal fa-th"></i><span>Details</span></Link>
              <br/>
              <span class="span">Blog</span>
              <Link to="/admin_dashboard/blog-category"><i class="fal fa-mail-bulk"></i><span>Category</span></Link>
              <Link to="/admin_dashboard/blog-content"><i class="fal fa-mail-bulk"></i><span>Details</span></Link>
              <br />
              <span class="span">News</span>
              <Link to="/admin_dashboard/news-category"><i class="fal fa-newspaper"></i><span>Category</span></Link>
              <Link to="/admin_dashboard/news-content"><i class="fal fa-newspaper"></i><span>Details</span></Link>
              <br />
              <span class="span">Others</span>
              <Link to="/admin_dashboard/users"><i class="fal fa-users"></i><span>Users</span></Link>
              <Link to="/admin_dashboard/feedback"><i class="fal fa-comment-alt-lines"></i><span>Feedback</span></Link>
              <Link to="/admin_dashboard/contact"><i class="fal fa-users"></i><span>Contact</span></Link>
              <i onClick={ this.handleLogOut } class="fal fa-sign-out-alt sign-out"></i>
              <div className="footer">

                  <p>Copyright @ 2020 | Sarvajna Info tech LLP</p>
              </div>
            </div>
            {/* sidebar end */}
            <div className="content">
              <Switch>
                <Route path="/admin_dashboard/Home" component={Home}></Route>
                <Route path="/admin_dashboard/shop-content" component={ shopContent}></Route>
                <Route path="/admin_dashboard/shop-category" component={ shopCategory}></Route>

                <Route path="/admin_dashboard/blog-content" component={ blogContent}></Route>
                <Route path="/admin_dashboard/blog-category" component={ blogCategory }></Route>

                <Route path="/admin_dashboard/news-content" component={ newsContent}></Route>
                <Route path="/admin_dashboard/news-category" component={ newsCategory }></Route>

                <Route path="/admin_dashboard/users" component={Users}></Route>
                <Route path="/admin_dashboard/feedback" component={Feedback}></Route>
                <Route path="/admin_dashboard/contact" component={Contact}></Route>
            </Switch>
              {/* <div className="footer">

                <p>Copyright @ 2020 | Sarvajna Info tech LLP</p>
              </div> */}
            </div>
          </Router>
            {/* <Switch>
              <div class="content">

                <Route path="#addDetails" component={addContent}/>


              </div>
            </Switch> */}
        </div>
      </>
    );
 }
}
