import React, { Component } from 'react'

export default class Message extends Component {
    render() {

        if (!this.props.show) {
            return null;
        }
        return (
            <div className={ this.props.alertHide }>
                <div className="modal-wrapper" >
                    <div className="modal-header">
                        <div className="success-icon">
                            <i class="fas fa-exclamation"></i>
                        </div>
                        {/* <h4>Delete</h4> */}
                        {/* <i class="fal fa-times close-btn" onClick={ this.props.handleClose }></i> */}

                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-3-col">
                                { this.props.children }
                            </div>
                        </div>
                        {/* <div className="modal-footer">
                            <button type="button" onClick={ this.props.handleAddDet } className="btn-footer">Add</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div> */}
                    </div>
                </div>
            </div>
        );

    }
}
