import React, { Component } from 'react'

import Axios from '../../../../axios'
import '../../Admin-css/Admin-Detail.css'
import Message from './Message'

export default class addContent extends Component {

    constructor(props) {
        super(props)

        let setImage = 0

        this.state = {
            categories: [],
            items : {
                Ac: false,
                DigitalPayment: false,
                Parking: false,
                hrs24: false,
            },
            name: '',
            category: '',
            subcategory: '',
            // profileimage: null,
            image: null,
            description: '',
            website: '',
            email: '',
            address: '',
            phone: '',

            formErrors: {},
            message: '',
            responseData: '',
            setImage,
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleCheckChange = this.handleCheckChange.bind(this);

        this.onUpload = this.onUpload.bind(this);
        // this.onProfileUpload = this.onProfileUpload.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);

    }


    componentDidMount(){

        Axios.get('/getCategory')
        .then( response =>{
            console.log(response)
            this.setState({categories: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }

    handleChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    refreshPage(){
        window.location.reload(false);
    }

    handleCheckChange = (e) =>{
       var {name, checked} = e.target;
       this.setState((e) =>{
           var selectedItem = e.items
           return selectedItem[name] = checked;
       })
    }

    onUpload(e){
        // let file = e.target.files[0];
        this.setState({
            image: e.target.files[0],
            setImage: 1,
        })

    }

    // onProfileUpload(e){
    //     this.setState({
    //         profileimage: e.target.files[0],

    //     })
    // }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }

    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { name,  category, description, address, phone} = this.state

        if (name === '') {
            formErrors.nameError = "Name is required!!";
            isValid = false;
        }

        if (category === '') {
            formErrors.categoryError = "Please Select the Category!!";
            isValid = false;
        }

        if (description === '') {
            formErrors.descriptionError = "Description is required!!";
            isValid = false;
        }
        if (address === '') {
            formErrors.addressError = "Address is required!!";
            isValid = false;
        }
        if (phone === '') {
            formErrors.phoneError = "Phone is required!!";
            isValid = false;
        }


        if (name.length === 1) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length === 2) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length === 3) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length > 50) {
            formErrors.nameError = "Name must be of length 50";
            isValid = false;
        }

        if (description.length > 3000) {
            formErrors.descriptionError = "Description must be of length 3000";
            isValid = false;
        }
        if (description.length < 10) {
            formErrors.descriptionError = "Description must be of 10 or more";
            isValid = false;
        }

        if (address.length === 1) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length === 2) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length === 3) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length > 150) {
            formErrors.addressError = "Address must be of length 150";
            isValid = false;
        }


        this.setState({ formErrors });
        return isValid;
    }

    handleSubmit = (e) =>{
        e.preventDefault();

        const isValid = this.handleFormValidation();

        if (isValid) {

            if (this.state.setImage === 1) {
                // let file = this.state.image
                let data = new FormData();
                data.append('image', this.state.image);
                // data.append('profileimage', this.state.profileimage);
                data.append('name', this.state.name);
                data.append('category', this.state.category);
                data.append('subcategory', this.state.subcategory);
                data.append('description', this.state.description);
                data.append('website', this.state.website);
                data.append('email', this.state.email);
                data.append('address', this.state.address);
                data.append('phone', this.state.phone);

                Axios.post(`/addContent/${ this.state.setImage}`, data)

                    .then(response => {
                        // console.log(response);
                        this.setState({
                            responseData: response.data.message
                        })
                        this.openMessage();

                    })
                    .catch(error => {
                        console.log(error);
                    })
            }
            else if (this.state.setImage === 0) {

                const data = {
                    name: this.state.name,
                    category: this.state.category,
                    subcategory: this.state.subcategory,
                    description: this.state.description,
                    website: this.state.website,
                    email: this.state.email,
                    address: this.state.address,
                    phone: this.state.phone,

                }

                Axios.post('/addContent', data)

                    .then(response => {
                        // console.log(response);
                        this.setState({
                            responseData: response.data.message
                        })
                        this.openMessage();

                    })
                    .catch(error => {
                        console.log(error);
                    })

            }
        }

    }


    render() {

        const { categories, responseData, formErrors} = this.state

        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";

        if (!this.props.show) {
            return null;
        }

        return (

            <div className={ this.props.showHide }>
                <div className="modal-wrapper" >
                    <div className="modal-header">
                        <h4>Add Shop Details </h4>
                        <i class="fal fa-times" onClick={ this.props.handleClose }></i>
                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-3-col">
                                <div className="form-box">
                                    <div className="div-input">
                                        <input class="form-control" type="text" name="name" onChange={this.handleChange} value={this.name} placeholder="* Shop Name"/>
                                        { formErrors.nameError &&
                                            <div class="input-errorMsg">{ formErrors.nameError }</div>
                                        }
                                    </div>
                                </div>

                                {/* <div className="form-box">
                                    <div className="div-input">
                                        <input class="form-control" type="file" onChange={this.onProfileUpload} name="profileimage" value={this.profileimage} accept=".png, .jpg" placeholder="Profile Image"/>
                                    </div>
                                </div>  */}

                                <div className="form-box">
                                    <div className="div-input">
                                    <select class="form-control" onChange={this.handleChange} name="category"   placeholder=""> 
                                        <option>Select Shop Category</option>
                                            {
                                                categories.length ?
                                                categories.map(category =>        
                                                    <option name={category.category_name} value={category.id} key={category.id}>{category.category_name}</option>                   
                                                ) : null
                                            }
                                        </select>
                                        { formErrors.categoryError &&
                                            <div class="input-errorMsg">{ formErrors.categoryError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">
                                    <div className="div-input">
                                        <input className="form-control" type="text" name="subcategory" onChange={ this.handleChange } value={ this.subcategory } placeholder="Sub Category" />
                                    </div>

                                </div>

                                <div className="form-box">

                                    <div className="div-input">
                                        <textarea rows="1" class="form-control" type="text" name="description" onChange={this.handleChange} value={this.description} placeholder="* Description"/>
                                        { formErrors.descriptionError &&
                                            <div class="input-errorMsg">{ formErrors.descriptionError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="file" onChange={this.onUpload} name="image" value={this.image} accept=".png, .jpg" placeholder="Image"/>
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="website" name="website" onChange={ this.handleChange } value={ this.website } placeholder="Website"/>
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="email" name="email" onChange={ this.handleChange } value={ this.email } placeholder="Email"/>
                                    </div>
                                </div>
                                 <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="address" onChange={this.handleChange} value={this.address} placeholder="* Address"/>
                                        { formErrors.addressError &&
                                            <div class="input-errorMsg">{ formErrors.addressError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="number" name="phone" onChange={this.handleChange} value={this.phone} autoComplete="off" placeholder="* Phone Number"/>
                                        { formErrors.phoneError &&
                                            <div class="input-errorMsg">{ formErrors.phoneError }</div>
                                        }
                                    </div>
                                </div>

                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" onClick={ this.handleSubmit } class="btn-footer">Submit</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div>
                    </div>
                </div>
                <Message show={ this.state.message } messageHide={ messageHide }>
                    <h3>Success !</h3>
                    <p>{ responseData }</p>
                    <div className="m-footer">
                        <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                    </div>
                </Message>
            </div>

        )
    }
}
