import React, { Component } from 'react'
import Axios from '../../../axios'
import '../Admin-css/Admin-Detail.css'
import Message from './Message'
import Alert from './AlertMessage'

export default class Feedback extends Component {

    constructor(props) {
        super(props)

        this.state = {
           feedbacks: [],
            message: false,
            alert:false,
            responseData: '',
            id: '',
        }
        this.refreshPage = this.refreshPage.bind(this);
        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);
        this.closeAlertMessage = this.closeAlertMessage.bind(this);
    }

    openMessage = (e) => {
        this.setState({
            message: true
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }


    openAlert = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }

    componentDidMount(){

        Axios.get('/getFeedback')
        .then( response =>{
            console.log(response)
            this.setState({feedbacks: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }

    refreshPage() {
        window.location.reload(false);
    }

    deleteFeedback(id){

        Axios.delete(`/deleteFeedback/${id}`)
            .then(response => {
                // console.log(response);
                this.setState({
                    alert: false,
                    responseData: response.data.message

                })
                this.openMessage();
            })
            .catch(error => {
                console.log(error);

                // console.log("error-----")
            })
    }

    render() {
        const { feedbacks, responseData} = this.state

        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        return (
            <>
                <div className="dash-detailClass">
                    <div className="class-title">
                        <h3><a href="/admin_dashboard/feedback">Feedback</a></h3>
                    </div>

                    <div className="section div-section">
                        <div className="div-class">
                            <div className="class-table">
                                <div className="section-title">
                                    <h5>Feedback List</h5>
                                </div>
                                    <table>
                                        <thead>
                                            <tr>
                                                <th>Type</th>
                                                <th>Description</th>
                                                <th>First Name</th>
                                                <th>Last Name</th>
                                                <th>Email</th>
                                                <th>Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>

                                            {
                                                feedbacks.length ?
                                                feedbacks.map(feedback => 
                                                    <tr key={feedback.fb_id}>
                                                        <td>{feedback.fb_type}</td>
                                                        <td>{feedback.fb_description}</td>
                                                        <td>{feedback.fb_firstname}</td>
                                                        <td>{feedback.fb_lastname}</td>
                                                        <td>{feedback.fb_email}</td>

                                                        <td class="action-btns"><button onClick={ this.openAlert.bind(this, feedback.fb_id) } class="div-action-btn"><i class="fas fa-trash"></i></button></td>
                                                    </tr>) : null
                                            }

                                        </tbody>
                                    </table>
                                    <Message show={ this.state.message } messageHide={ messageHide }>
                                        <h3>Success !</h3>
                                        <p>{ responseData }</p>
                                        <div className="m-footer">
                                            <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                                        </div>
                                    </Message>

                                    <Alert show={ this.state.alert } alertHide={ alertHide }>
                                        <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                                        <div className="m-footer">

                                            <button type="button" onClick={ this.deleteFeedback.bind(this, this.state.id) } className="btn-footer">Ok</button>
                                            <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                                        </div>
                                    </Alert>
                            </div>

                        </div>
                    </div>
                </div>

            </>
        )
    }
}
