import React, { Component } from 'react'
import ShowMoreText from 'react-show-more-text';
import '../../Admin-css/Admin-Detail.css'
import Axios from '../../../../axios'

import Edit from './Edit'
import Message from './Message'
import Alert from './AlertMessage'

export default class viewContent extends Component {

    constructor (props) {
        super(props)

        let setImage = 0

        this.state = {
            contents: [],
            categories: [],

            show: false,
            message: false,
            editMode: false,

            id: '',

            name: '',
            category: '',
            subcategory: '',
            profileimage: null,
            image: null,
            description: '',
            website: '',
            email: '',
            address: '',
            phone: '',

            filter: '',

            responseData: '',
            formErrors: {},

            setImage,

        }
        this.handleChange = this.handleChange.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);

        this.onUpload = this.onUpload.bind(this);

        this.triggerInputFile = this.triggerInputFile.bind(this);
        this.triggerProfileInputFile = this.triggerProfileInputFile.bind(this);

        this.refreshPage = this.refreshPage.bind(this);
        this.handleFilter = this.handleFilter.bind(this);

        this.closeModal = this.closeModal.bind(this);
        this.openModal = this.openModal.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.closeAlertMessage = this.closeAlertMessage.bind(this);

        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    componentDidMount() {

        Axios.get('/getContent')
            .then(response => {
                console.log(response)
                this.setState({ contents: response.data })
            })
            .catch(error => {
                console.log(error)
            })

        Axios.get('/getCategory')
            .then(response => {
                console.log(response)
                this.setState({ categories: response.data })
            })
            .catch(error => {
                console.log(error)
            })
    }

    handleFilter(e) {
        console.log(e);

        this.setState({

            filter: e.target.value
        })
        console.log(this.state.filter)

    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    onUpload(e) {

        this.setState({
            image: e.target.files[0],
            setImage: 1,
        })
        console.log(e.target.files[0]);
    }

    // onProfileUpload(e) {
    //     console.log(e.target.files);
    //     this.setState({
    //         profileimage: e.target.files[0],

    //     })
    // }

    executeOnClick(isExpanded) {
        console.log(isExpanded);
    }

    openModal = (e) => {
        this.setState({
            show: true
        })
    }

    closeModal = (e) => {
        this.setState({
            show: false
        })
    }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }

    openAlertMessage = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }

    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { name, category, description, address, phone } = this.state

        if (name === '') {
            formErrors.nameError = "Name is required!!";
            isValid = false;
        }

        if (category === '') {
            formErrors.categoryError = "Please Select the Category!!";
            isValid = false;
        }

        if (description === '') {
            formErrors.descriptionError = "Description is required!!";
            isValid = false;
        }
        if (address === '') {
            formErrors.addressError = "Address is required!!";
            isValid = false;
        }
        if (phone === '') {
            formErrors.phoneError = "Phone is required!!";
            isValid = false;
        }


        if (name.length === 1) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length === 2) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length === 3) {
            formErrors.nameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (name.length > 50) {
            formErrors.nameError = "Name must be of length 50";
            isValid = false;
        }

        if (description.length > 3000) {
            formErrors.descriptionError = "Description must be of length 3000";
            isValid = false;
        }
        if (description.length < 10) {
            formErrors.descriptionError = "Description must be of 10 or more";
            isValid = false;
        }

        if (address.length === 1) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length === 2) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length === 3) {
            formErrors.addressError = "Address must be of of 4 or more";
            isValid = false;
        }
        if (address.length > 150) {
            formErrors.addressError = "Address must be of length 150";
            isValid = false;
        }


        this.setState({ formErrors });
        return isValid;
    }


    deleteContent(id) {

        Axios.delete(`/deleteContent/${ id }`)
            .then(response => {

                this.setState({
                    alert: false,
                    responseData: response.data.message
                })

                this.openMessage();
            })
            .catch(error => {
                console.log(error);

            })
    }


    editContent = async (id) => {

        let editingItem = this.state.contents.find(content => {
            return content.content_id === id;
        })

        console.log(editingItem);
        console.log(editingItem.profileImage)

        await this.setState({
            name: editingItem.name,
            // profileimage: editingItem.profileImage.split("/").pop(),
            category: editingItem.categoryName,
            subcategory: editingItem.subCategory,

            image: editingItem.image.split("/").pop(),
            description: editingItem.description,
            website: editingItem.website,
            email: editingItem.email,
            address: editingItem.address,
            phone: editingItem.phone,
            editMode: id
        })

        this.openModal();

        console.log(this.state.profileimage)

    }


    handleUpdate(e) {
        e.preventDefault();

        const isValid = this.handleFormValidation();
        if (isValid) {

                let id = this.state.editMode;

                this.setState({
                    editMode: true,

                })

                if (this.state.setImage === 1) {

                let data = new FormData();
                data.append('image', this.state.image);
                // data.append('profileimage', this.state.profileimage);

                data.append('name', this.state.name);
                data.append('category', this.state.category);
                data.append('subcategory', this.state.subcategory);
                data.append('description', this.state.description);
                data.append('website', this.state.website);
                data.append('email', this.state.email);
                data.append('address', this.state.address);
                data.append('phone', this.state.phone);

                Axios.put(`/editContent/${ id }/${ this.state.setImage}`, data)

                    .then(response => {
                        console.log(response);

                        this.setState({
                            responseData: response.data.message
                        })
                        this.openMessage();
                    })
                    .catch(error => {
                        console.log(error);
                    })
                }

                else if (this.state.setImage === 0) {

                    const data = {
                        name: this.state.name,
                        category: this.state.category,
                        subcategory: this.state.subcategory,
                        description: this.state.description,
                        website: this.state.website,
                        email: this.state.email,
                        address: this.state.address,
                        phone: this.state.phone,
                    }

                    Axios.post('/addContent', data)

                        .then(response => {
                            // console.log(response);
                            this.setState({
                                responseData: response.data.message
                            })
                            this.openMessage();

                        })
                        .catch(error => {
                            console.log(error);
                        })
                }
        }
    }

    triggerInputFile () { this.fileInput.click()}
    triggerProfileInputFile() { this.fileProfileInput.click() }



    render() {
        const { contents, categories, filter, formErrors, responseData } = this.state

        const showHide = this.state.show ? "edit-form display-block" : "edit-form display-none";
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        return (
            <>
                <div class="class-table">

                    <div className="section-title">
                        <h5>Shop List</h5>
                    </div>

                        {/* <div className="view-card">
                            <h3>All Contents</h3>
                            <div className="table-grid">
                                <div className="form-box">

                                    <div className="div-select">
                                        <select class="form-control" onChange={ this.handleFilter } value={ filter } required>
                                            <option>Select Category</option>{
                                                categories.length ?
                                                    categories.map(category =>

                                                        <option name={ category.category_name } value={ category.category_name } key={ category.id }>{ category.category_name }</option>
                                                    ) : null
                                            }
                                        </select>
                                    </div>
                                </div>
                            </div><br/> 
                        </div> */}
                    <table>
                        <thead>
                            <tr>
                                <th>Image</th>
                                <th>Name</th>
                                <th>Category Name</th>

                                <th>Description</th>

                                <th>Address</th>
                                <th>Phone</th>
                                <th>Action</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {

                                contents.filter((content) => !filter || content.categoryName === filter).map(content => <tr key={ content.id }>
                                    <td><img src={ content.image.replace('/public', '') } alt="" /></td>
                                    <td>{ content.name }</td>
                                    <td>{ content.categoryName }</td>

                                    <td>
                                        <ShowMoreText

                                            lines={ 1 }
                                            more='Show more'
                                            less='Show less'
                                            className='content-css'
                                            anchorClass='my-anchor-css-class'
                                            onClick={ this.executeOnClick }
                                            expanded={ false }
                                            width={ 280 }
                                        >{ content.description }</ShowMoreText>
                                    </td>

                                    <td>{ content.address }</td>
                                    <td>{ content.phone }</td>
                                    <td>
                                        <div class="action-btns">
                                            <button class="div-action-btn" onClick={ this.editContent.bind(this, content.content_id) }><i class="fas fa-pen"></i></button>
                                            <button onClick={ this.openAlertMessage.bind(this, content.content_id) } class="div-action-btn"><i class="fas fa-trash"></i></button>
                                        </div>
                                    </td>

                                </tr>)
                            }
                        </tbody>
                    </table>

                    <Edit show={ this.state.show } handleClose={ this.closeModal } handleSubmit={ this.handleUpdate } showHide={ showHide }>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="text" name="name" onChange={ this.handleChange } value={ this.state.name } placeholder="Name" />
                                { formErrors.nameError &&
                                    <div class="input-errorMsg">{ formErrors.nameError }</div>
                                }
                            </div>
                        </div>

                        <div className="form-box">
                            <div className="div-input">
                                <select class="form-control" onChange={ this.handleChange } name="category" value={ this.state.category }>
                                    <option>Select Category</option>{
                                        categories.length ?
                                            categories.map(category =>

                                                <option name={ category.category_name } value={ category.category_name } key={ category.id }>{ category.category_name }</option>
                                            ) : null
                                    }
                                </select>
                                { formErrors.categoryError &&
                                    <div class="input-errorMsg">{ formErrors.categoryError }</div>
                                }
                            </div>
                        </div>

                        <div className="form-box">

                            <div className="div-input">
                                <input className="form-control" type="text" name="subcategory" onChange={ this.handleChange } value={ this.state.subcategory } placeholder="Sub Category" />
                            </div>

                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" value={ this.state.image ? this.state.image.name : '' } onClick={ this.triggerInputFile } placeholder="Image"/>
                                <input ref={ fileInput => this.fileInput = fileInput } Style={ "display : none" } type="file" onChange={ this.onUpload } name="image" accept=".png, .jpg" />
                            </div>
                        </div>

                        <div className="form-box">

                            <div className="div-input">
                                <textarea rows="1" class="form-control" type="text" name="description" onChange={ this.handleChange } value={ this.state.description } placeholder="Description" />
                                { formErrors.descriptionError &&
                                    <div class="input-errorMsg">{ formErrors.descriptionError }</div>
                                }
                            </div>
                        </div>

                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="website" name="website" onChange={ this.handleChange } value={ this.state.website } placeholder="Website"/>
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="email" name="email" onChange={ this.handleChange } value={ this.state.email } placeholder="Email"/>
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="text" name="address" onChange={ this.handleChange } value={ this.state.address } placeholder="Address" />
                                { formErrors.addressError &&
                                    <div class="input-errorMsg">{ formErrors.addressError }</div>
                                }
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="number" name="phone" onChange={ this.handleChange } value={ this.state.phone } autoComplete="off" placeholder="Phone" />
                                { formErrors.phoneError &&
                                    <div class="input-errorMsg">{ formErrors.phoneError }</div>
                                }
                            </div>
                        </div>

                    </Edit>

                    <Message show={ this.state.message } messageHide={ messageHide }>
                        <h3>Success !</h3>
                        <p>{ responseData }</p>
                        <div className="m-footer">
                            <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                        </div>
                    </Message>

                    <Alert show={ this.state.alert } alertHide={ alertHide }>
                        <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                        <div className="m-footer">

                            <button type="button" onClick={ this.deleteContent.bind(this, this.state.id) } className="btn-footer">Ok</button>
                            <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                        </div>
                    </Alert>
                </div>
            </>
        )
    }
}
