import React, { Component } from 'react'
import Axios from '../../../axios'
import '../Admin-css/Admin-Detail.css'
import Message from './Message'
import Alert from './AlertMessage'

export default class Contact extends Component {

    constructor(props) {
        super(props)

        this.state = {
           contacts: [],  
            message: false,
            alert: false,
            responseData: '',
            id: '',
        }
        this.refreshPage = this.refreshPage.bind(this);
        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);
        this.closeAlertMessage = this.closeAlertMessage.bind(this);
    }

    componentDidMount(){

        Axios.get('/getContact')
        .then( response =>{
            console.log(response)
            this.setState({contacts: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }

    refreshPage() {
        window.location.reload(false);
    }

    openMessage = (e) => {
        this.setState({
            message: true
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }


    openAlert = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }


    deleteContact(id){

        Axios.delete(`/deleteContact/${id}`)
            .then(response => {
                // console.log(response);
                this.setState({
                    alert: false,
                    responseData: response.data.message

                })
                this.openMessage();
            })
            .catch(error => {
                console.log(error);

                // console.log("error-----")
            })
    }

    render() {
        const { contacts, responseData} = this.state

        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        return (
            <>
                <div className="dash-detailClass">
                    <div className="class-title">
                        <h3><a href="/admin_dashboard/feedback">Contact</a></h3>
                    </div>

                    <div className="section div-section">
                        <div className="div-class">
                            <div className="class-table">
                                <div className="section-title">
                                    <h5>Contact List</h5>
                                </div>
                                    <table>
                                        <thead>
                                            <tr>

                                                <th>Name</th>
                                                <th>Email</th>
                                                <th>Phone</th>
                                                <th>Message</th>
                                                <th>Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>

                                            {
                                                contacts.length ?
                                                contacts.map(contact => 
                                                    <tr key={contact.id}>
                                                        <td>{contact.name}</td>
                                                        <td>{contact.email}</td>
                                                        <td>{contact.phone}</td>
                                                        <td>{contact.message}</td>


                                                        <td class="action-btns"><button onClick={ this.openAlert.bind(this, contact.id) } class="div-action-btn"><i class="fas fa-trash"></i></button></td>
                                                    </tr>) : null
                                            }

                                        </tbody>
                                    </table>
                                <Message show={ this.state.message } messageHide={ messageHide }>
                                    <h3>Success !</h3>
                                    <p>{ responseData }</p>
                                    <div className="m-footer">
                                        <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                                    </div>
                                </Message>

                                <Alert show={ this.state.alert } alertHide={ alertHide }>
                                    <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                                    <div className="m-footer">

                                        <button type="button" onClick={ this.deleteContact.bind(this, this.state.id) } className="btn-footer">Ok</button>
                                        <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                                    </div>
                                </Alert>
                            </div>

                        </div>
                    </div>
                </div>
            </>
        )
    }
}
