import React, { Component } from 'react'
import './Content.css'
import Axios from '../../../axios'
import Modal from 'react-bootstrap/Modal'
import './addContent.css'

export default class Content extends Component {

    constructor(props) {
        super(props)

        this.state = {
            categories: [],
            items : {
                Ac: false,
                DigitalPayment: false,
                Parking: false,
                hrs24: false,
            },
            name: '',
            category: '',
            subcategory: '',
            profileimage: null,
            image: null,
            description: '',
            website: '',
            email: '',
            address: '',
            phone: '',
            gallery: null,
            successMsg: false,
            // uploadedFile: null
            limit: 1,
            disabled: false
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleCheckChange = this.handleCheckChange.bind(this);
        this.onUpload = this.onUpload.bind(this);
         this.onProfileUpload = this.onProfileUpload.bind(this);
        this.closeMsg = this.closeMsg.bind(this);
    }


    componentDidMount(){

        Axios.get('/getCategory')
        .then( response =>{

            this.setState({categories: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }

    handleChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    handleCheckChange = (e) =>{
       var {name, checked} = e.target;
       this.setState((e) =>{
           var selectedItem = e.items
           return selectedItem[name] = checked;
       })
    }

    onUpload(e){
        // let file = e.target.files[0];
        this.setState({
            image: e.target.files[0],

        })

    }

    onProfileUpload(e){
        this.setState({
            profileimage: e.target.files[0],
        })
    }

    handleSubmit = (e) =>{
        e.preventDefault();

        // let file = this.state.image
        let data = new FormData();
        data.append('image', this.state.image);
        data.append('profileimage', this.state.profileimage);
        data.append('name', this.state.name);
        data.append('category', this.state.category);
        data.append('subcategory', this.state.subcategory);
        data.append('description', this.state.description);
        data.append('website', this.state.website);
        data.append('email', this.state.email);
        data.append('address', this.state.address);
        data.append('phone', this.state.phone);
        data.append('gallery', this.state.gallery);

        Axios.post('/addContent', data)
        .then(response =>{

             this.setState({
                successMsg: true,
                disabled: true
            })
            this.image.value = "";
            this.profileimage.value = "";
            this.name.value = "";
            this.category.value = "";
            this.subcategory.value = "";
            this.description.value = "";
            this.website.value = "";
            this.email.value = "";
             this.address.value = "";
            this.phone.value = "";

        })
        .catch(error =>{
            console.log(error);
        })

    }

    closeMsg(){
        this.setState({
            successMsg: false,

        })
    }
    render() {
        const {categories, successMsg} = this.state
        return (
           <>
            <div class="div-content">
                <h3>Cont<span>ent</span></h3>
                <div class="addContent">
                {/* <h2>Add Content</h2> */}
                <form method="POST" onSubmit={this.handleSubmit} enctype="multipart/form-data">
                    <div className="div-card">
                        <div className="div-card-head">
                            {/* <img class="img-fluid" alt="" src={process.env.PUBLIC_URL + './img/icon.png'}/> */}
                            <h3>Info</h3>
                        </div>
                        <hr/>
                        <div className="div-card-row">
                            <div className="div-col-left">

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Name</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="text" name="name" onChange={this.handleChange} value={this.name} required/>
                                    </div>
                                </div>

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Profile Image</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="file" onChange={this.onProfileUpload} name="profileimage" value={this.profileimage} accept=".png, .jpg" required/>
                                    </div>
                                </div> 

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Category</label>
                                    </div>
                                    <div className="div-select">
                                    <select class="form-control" onChange={this.handleChange} name="category"   required> 
                                    <option>Select Category</option>{
                                                categories.length ?
                                                categories.map(category => 

                                                        <option name={category.category_name} value={category.id} key={category.id}>{category.category_name}</option>                   
                                                    ) : null
                                            }
                                        </select>
                                    </div>
                                </div>   
                            </div>

                            <div className="div-col-right">

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Sub Category</label>
                                    </div>
                                    <div className="div-select">
                                    <select class="form-control" onChange={this.handleChange} name="subcategory"   required> 
                                    <option>Select Sub Category</option>{
                                                categories.length ?
                                                categories.map(category => 

                                                        <option name={category.sub_category} value={category.id} key={category.id}>{category.sub_category}</option>                   
                                                    ) : null
                                            }
                                        </select>
                                    </div>
                                </div>

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Description</label>
                                    </div>
                                    <div className="div-input">
                                        <textarea rows="2" class="form-control" type="text" name="description" onChange={this.handleChange} value={this.description} required/>
                                    </div>
                                </div>
                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Description Image</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="file" onChange={this.onUpload} name="image" value={this.image} accept=".png, .jpg" required/>
                                    </div>
                                </div> 
                            </div>
                        </div>
                    </div><br/>
                    <div className="div-card">
                        <div className="div-card-head">
                            {/* <img class="img-fluid" alt="" src={process.env.PUBLIC_URL + '../img/icon.png'}/> */}
                            <h3>Contact Details</h3>
                        </div>
                        <hr/>
                        <div className="div-card-row">
                            <div className="div-col-left">
                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Website</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="website" name="website" onChange={this.handleChange} value={this.website}/>
                                    </div>
                                </div>
                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Email</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="email" name="email" onChange={this.handleChange} value={this.email}/>
                                    </div>
                                </div>

                            </div>
                            <div className="div-col-right">

                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Address</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="text" name="address" onChange={this.handleChange} value={this.address} required/>
                                    </div>
                                </div>
                                <div className="form-box">
                                    <div className="div-label">
                                        <label>Phone Number</label>
                                    </div>
                                    <div className="div-input">
                                        <input class="form-control" type="number" name="phone" onChange={this.handleChange} value={this.phone} required/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <br/>
                    {/* <div className="div-card">
                        <div className="div-card-head">

                            <h3>Amenities</h3>
                        </div>
                        <hr/>
                        <div className="div-card-row div-checkbox">
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" name="Ac" onChange={this.handleCheckChange} value={this.Ac}/>
                                <label class="form-check-label">Ac</label>
                            </div><br/>
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" name="DigitalPayment" onChange={this.handleCheckChange} value={this.DigitalPayment}/>
                                <label class="form-check-label">Digital Payment</label>
                            </div>
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" name="Parking" onChange={this.handleCheckChange} value={this.Parking}/>
                                <label class="form-check-label">Parking</label>
                            </div>
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" name="AcceptsCards" onChange={this.handleCheckChange} value={this.AcceptsCards}/>
                                <label class="form-check-label">Accepts Credit/Debit Cards</label>
                            </div>
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" name="hrs24" onChange={this.handleCheckChange} value={this.hrs24}/>
                                <label class="form-check-label">24 hrs</label>
                            </div>
                        </div>
                    </div> */}


                    <div className="div-card">
                    <button type="submit" disabeled ={this.disabled}  class="submit-btn">Submit</button>
                </div>
                </form>
                <Modal
                    size="lg"
                    show={successMsg}
                    onHide={this.closeMsg}
                    centered

                >
                    <Modal.Header closeButton>

                    </Modal.Header>
                    <Modal.Body>
                        <p>Content Added Successfully</p>
                    </Modal.Body>

                </Modal>
            </div>
            </div>
           </>
        )
    }
}
