import React, { Component, Fragment } from 'react'
import Header from '../Header/Header2'
import './Login.css'
import Axios from '../../axios'
import { Redirect } from 'react-router-dom'
// import Signup from './Signup'
// import Signin from './Signin'
// import { BrowserRouter as Router, Route, Redirect} from 'react-router-dom';
import Helmet from 'react-helmet'

export default class Login extends Component {

    // onNavigateHome(){
    //     browserHistory.push("/")
    // }

    constructor(props) {
        super(props)

        this.state = {
            type:'User',
            email: '',
            password: '',
            loginStatue: false,
            // LoggedIn: false
            redirect: null
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        // this.userAuthenticated = this.userAuthenticated(this);
        Axios.defaults.withCredentials = true
    }

    // componentDidMount(){
    //     Axios.get('http://localhost:4040/login')
    //     .then( (response) =>{
    //         // console.log(response);
    //         if(response.data.auth === true){
    //                 this.setState({
    //                 loginStatus : response.data.user[0].email
    //             })
    //         }else{
    //             this.setState({
    //                 loginStatus : response.data.message
    //             })
    //         }

    //     }).catch(error =>{
    //         console.log(error);
    //     })


    // }


    handleChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    handleSubmit = (e) =>{

        e.preventDefault();
        const {email, password} = this.state;

        Axios.post('/login',{
            headers: {
                'Content-Type': 'application/json',
                "x-access-token": localStorage.getItem("token")
            },
            email: email,
            password: password,
        })
        .then(response =>{
            // console.log("From Login", response)
            // alert("LoggedIn")
            // if(!response.data.message){
                console.log("------------")
                console.log(response.data);
            if(!response.data.auth){
                this.setState({
                    // loginStatus : response.data.message,
                    loginStatus : false,
                    // LoggedIn: true
                })

            } else {
                // console.log(response)
                localStorage.setItem("token", response.data.token)
                this.setState({
                    // loginStatus : response.data[0].email
                    loginStatus : true,
                    redirect: "/user_dashboard/Home"

                });

            }
        })
        .catch(error =>{
            console.log("Login Error", error)
        })

    }

    // userAuthenticated = () =>{
    //     Axios.get('http://localhost:4040/isUserAuth', {
    //         headers: {
    //             'Content-Type': 'application/json',
    //             "x-access-token": localStorage.getItem("token")
    //         }
    //     })
    //     .then((response) =>{
    //         console.log(response);
    //         // this.setState({
    //         //     // loginStatus : response.data[0].email



    //         // });

    //     })
    // }

    render() {
        const {loginStatus, redirect} = this.state
        // if(loginStatus === true){
        //     <Redirect to="/user_dashboard/Home"/>
        // }
        // else {
        //     <Redirect to="/login"/>
        // }
        // if(this.state.LoggedIn){
        //     return (<Redirect to ="/user_dashboard"/> );
        // }
        // <Route exact path ="/login">{this.state.loginStatus ? <Redirect to ="/user_dashboard"/> : <Redirect to="/login"/>}</Route>

        if (loginStatus === true) {
            return <Redirect to={redirect} />
        } 
        return (
            <Fragment>
                {/* {loginStatus ? (<Redirect to="/user_dashboard/Home"/>): ( */}
                <div>
                    <Helmet>
                        <title>User Login</title>
                        <meta name="description" content="User Login" />
                    </Helmet>
                    <Header/>
                    <div className="login">
                        <div className="login-banner"> 

                            <section className="div-login-main">

                                {/* <Link to="/login">SignIn</Link> */}

                                    {/* <Route path="/login" as component={Signin}>

                                    </Route>
                                    <Route path="/register" as component={Signup}>

                                    </Route> */}

                                <form className="div-form">
                                    <div className="login-title">
                                        <h2>Login</h2> 
                                    </div>

                                    <div className="form-box">
                                        <div className="div-label">
                                            <label>Email</label>
                                        </div>
                                        <div className="div-input">
                                            <input class="form-control" type="email" name="email" onChange={this.handleChange} value={this.email} required/>
                                        </div>
                                    </div>
                                    <div className="form-box">
                                        <div className="div-label">
                                            <label>Paasword</label>
                                        </div>
                                        <div className="div-input">
                                            <input class="form-control" type="password" name="password" onChange={this.handleChange} value={this.password} required/>
                                        </div>
                                    </div>
                                    <div className="div-row">
                                        <a class="text-left" href="/">Forgot Password</a>

                                    </div>
                                    <div className="div-card">
                                        <button onClick={this.handleSubmit} class="submit-btn">Login</button>
                                    </div>
                                    {/* {loginStatus && (<button onClick={this.userAuthenticated} class="btn btn-primary" >Check if Authenticated</button>)} */}

                                </form>


                            </section>
                        </div>
                    </div>

                </div>
                {/* )} */}
            </Fragment>
        )
    }
}
