import React, { Component } from 'react'
import Axios from '../../../axios'
import '../Admin-css/Admin-Detail.css'
import Message from './Message'

export default class editForm extends Component {

    constructor(props) {
        super(props)

        let setImage = 0

        this.state = {
            type: 'User',
            username: '',
            email: '',
            phonenumber: '',
            password: '',
            confirmpassword: '',

            message: '',
            responseData: '',
            formErrors: {},
            setImage,
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }


    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { username, email, phonenumber, password, confirmpassword } = this.state

        if (username === '') {
            formErrors.usernameError = "Username is Required!!";
            isValid = false;
        }
        if (email === '') {
            formErrors.emailError = "Email is Required!!";
            isValid = false;
        }
        if (phonenumber === '') {
            formErrors.phonenumberError = "Phone Number is Required!!";
            isValid = false;
        }
        if (password === '') {
            formErrors.passwordError = "Password is Required!!";
            isValid = false;
        }
        if (confirmpassword === '') {
            formErrors.confirmpasswordError = "Confirm Password is Required!!";
            isValid = false;
        }


        if (username.length === 1) {
            formErrors.usernameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (username.length === 2) {
            formErrors.usernameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (username.length === 3) {
            formErrors.usernameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (username.length > 250) {
            formErrors.usernameError = "Title must be of length 250";
            isValid = false;
        }
        if (username.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.usernameError = "No Special Characters";
            isValid = false;
        }


        if (password.length === 1) {
            formErrors.passwordError = "Password must be of of 6 or more";
            isValid = false;
        }
        if (password.length === 2) {
            formErrors.passwordError = "Password must be of of 6 or more";
            isValid = false;
        }
        if (password.length === 3) {
            formErrors.passwordError = "Password must be of of 6 or more";
            isValid = false;
        }
        if (password.length === 4) {
            formErrors.passwordError = "Password must be of of 6 or more";
            isValid = false;
        }
        if (password.length === 5) {
            formErrors.passwordError = "Password must be of of 6 or more";
            isValid = false;
        }

        if (password.length > 10) {
            formErrors.passwordError = "Password must be of length 10";
            isValid = false;
        }


        this.setState({ formErrors });
        return isValid;
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const isValid = this.handleFormValidation();

        if (isValid) {

            const data = {
                username: this.state.username,
                email: this.state.email,
                phonenumber: this.state.phonenumber,
                password: this.state.password,
                confirmpassword: this.state.confirmpassword,

            }
            Axios.post(`/register`, data)
                .then(response => {
                    // console.log(response);
                    this.setState({
                        responseData: response.data.message
                    })
                    this.openMessage();

                })
                .catch(error => {
                    console.log(error);
                })

        }
    }




    render() {

        const { formErrors, responseData } = this.state
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";

        if (!this.props.show) {
            return null;
        }
        return (
            <div className={ this.props.showHide }>
                <div className="modal-wrapper" >
                    <div className="modal-header">
                        <h4>Add User</h4>
                        <i class="fal fa-times" onClick={ this.props.handleClose }></i>
                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-3-col">
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="username" onChange={ this.handleChange } value={ this.username } placeholder="Username"/>
                                        { formErrors.usernameError &&
                                            <div class="input-errorMsg">{ formErrors.usernameError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="email" name="email" onChange={ this.handleChange } value={ this.email } placeholder="Email" />
                                        { formErrors.emailError &&
                                            <div class="input-errorMsg">{ formErrors.emailError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="phonenumber" onChange={ this.handleChange } value={ this.phonenumber } placeholder="Phone Number"/>
                                        { formErrors.phonenumberError &&
                                            <div class="input-errorMsg">{ formErrors.phonenumberError }</div>
                                        }
                                    </div>
                                </div>

                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="password" onChange={ this.handleChange } value={ this.password } placeholder="Password"/>
                                        { formErrors.passwordError &&
                                            <div class="input-errorMsg">{ formErrors.passwordError }</div>
                                        }
                                    </div>
                                </div>

                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="confirmpassword" onChange={ this.handleChange } value={ this.confirmpassword } placeholder="Confirm Password"/>
                                        { formErrors.confirmpasswordError &&
                                            <div class="input-errorMsg">{ formErrors.confirmpasswordError }</div>
                                        }
                                    </div>
                                </div>

                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" onClick={ this.handleSubmit } class="btn-footer">Submit</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div>
                    </div>
                </div>
                <Message show={ this.state.message } messageHide={ messageHide }>
                    <h3>Success !</h3>
                    <p>{ responseData }</p>
                    <div className="m-footer">
                        <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                    </div>
                </Message>
            </div>
        );

    }
}
