import React, { Component } from 'react'

export default class editForm extends Component {
    render() {

        if (!this.props.show) {
            return null;
        }
        return (
            <div className="edit-form edit-sForm">
                <div className={ this.props.showHide } >
                    <div className="modal-header">
                        <h4>Edit Form </h4>
                        <i class="fal fa-times" onClick={ this.props.handleClose }></i>
                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-1-col">
                                { this.props.children }
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" onClick={ this.props.handleSubmit } className="btn-footer">Update</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div>
                    </div>
                </div>
            </div>
        );

    }
}
