import React, { Component } from 'react'
import Axios from '../../../../axios'
import '../../Admin-css/Admin-Detail.css'
import Message from './Message'

export default class editForm extends Component {

    constructor(props) {
        super(props)

        let setImage = 0

        this.state = {
            blogpost: [],
            bcategories: [],

            post_name: '',
            post_category: '',
            post_subcategory: '',
            post_image: null,
            post_description: '',
            post_date: new Date().toLocaleString(),
            post_authname: '',

            message: '',

            responseData: '',

            formErrors: {},

            setImage,
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.onUpload = this.onUpload.bind(this);

        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    componentDidMount() {

        Axios.get('/getPost')
            .then(response => {
                // console.log(response)
                this.setState({ blogpost: response.data })
            })
            .catch(error => {
                console.log(error)
            })

        Axios.get('/getPostCategory')
            .then(response => {
                // console.log(response)
                this.setState({ bcategories: response.data })
            })
            .catch(error => {
                console.log(error)
            })
    }

    onUpload(e) {

        this.setState({
            post_image: e.target.files[0],
            setImage: 1,
        })
        console.log(e.target.files[0]);
    }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }


    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { post_name, post_category, post_subcategory, post_description, post_authname } = this.state

        if (post_name === '') {
            formErrors.post_nameError = "Blog Title is Required!!";
            isValid = false;
        }
        if (post_category === '') {
            formErrors.post_categoryError = "Blog Category is Required!!";
            isValid = false;
        }
        if (post_subcategory === '') {
            formErrors.post_subcategoryError = "Blog Sub Category is Required!!";
            isValid = false;
        }
        if (post_description === '') {
            formErrors.post_descriptionError = "Blog Description is Required!!";
            isValid = false;
        }
        if (post_authname === '') {
            formErrors.post_authnameError = "Name is Required!!";
            isValid = false;
        }


        if (post_subcategory.length === 1) {
            formErrors.post_subcategoryError = "Sub Category must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length === 2) {
            formErrors.post_subcategoryError = "Sub Category must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length === 3) {
            formErrors.post_subcategoryError = "Sub Category must be of of 4 or more";
            isValid = false;
        }
        if (post_subcategory.length > 50) {
            formErrors.post_subcategoryError = "Sub Category must be of length 50";
            isValid = false;
        }
        if (post_subcategory.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_subcategoryError = "No Special Characters";
            isValid = false;
        }

        if (post_name.length === 1) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length === 2) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length === 3) {
            formErrors.post_nameError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (post_name.length > 250) {
            formErrors.post_nameError = "Title must be of length 250";
            isValid = false;
        }
        if (post_name.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_nameError = "No Special Characters";
            isValid = false;
        }

        if (post_description.length > 2000) {
            formErrors.post_descriptionError = "Description must be of length 2000";
            isValid = false;
        }
        if (post_description.length < 10) {
            formErrors.post_descriptionError = "Description must be of 10 or more";
            isValid = false;
        }

        if (post_authname.length === 1) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length === 2) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length === 3) {
            formErrors.post_authnameError = "Name must be of of 4 or more";
            isValid = false;
        }
        if (post_authname.length > 30) {
            formErrors.post_authnameError = "Name must be of length 30";
            isValid = false;
        }
        if (post_authname.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.post_authnameError = "No Special Characters";
            isValid = false;
        }



        this.setState({ formErrors });
        return isValid;
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const isValid = this.handleFormValidation();

        if (isValid) {
            if(this.state.setImage === 1){

            console.log(this.state.setImage);
            let data = new FormData();
            data.append('post_name', this.state.post_name);
            data.append('post_category', this.state.post_category);
            data.append('post_subcategory', this.state.post_subcategory);
            data.append('post_image', this.state.post_image);
            data.append('post_description', this.state.post_description);
            data.append('post_date', this.state.post_date);
            data.append('post_authname', this.state.post_authname);


            Axios.post(`/addPost/${ this.state.setImage}`, data)
                .then(response => {
                    // console.log(response);
                    this.setState({
                        responseData: response.data.message
                    })
                    this.openMessage();

                })
                .catch(error => {
                    console.log(error);
                })
            }

            else if(this.state.setImage === 0){

                const data = {
                    post_name: this.state.post_name,
                    post_category: this.state.post_category,
                    post_subcategory: this.state.post_subcategory,
                    post_description: this.state.post_description,
                    post_date: this.state.post_date,
                    post_authname: this.state.post_authname,

                }
                Axios.post(`/addPost`, data)
                    .then(response => {
                        // console.log(response);
                        this.setState({
                            responseData: response.data.message
                        })
                        this.openMessage();

                    })
                    .catch(error => {
                        console.log(error);
                    })
            }
        }
    }




    render() {

        const { formErrors, bcategories, responseData } = this.state
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";

        if (!this.props.show) {
            return null;
        }
        return (
            <div className={ this.props.showHide }>
                <div className="modal-wrapper" >
                    <div className="modal-header">
                        <h4>Add Blog Details</h4>
                        <i class="fal fa-times" onClick={ this.props.handleClose }></i>
                    </div>
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className="grid-3-col">
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="post_name" onChange={ this.handleChange } value={ this.post_name } placeholder="Blog Title"/>
                                        { formErrors.post_nameError &&
                                            <div class="input-errorMsg">{ formErrors.post_nameError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <select class="form-control" onChange={ this.handleChange } name="post_category">
                                            <option>Blog Category</option>{
                                                bcategories.length ?
                                                    bcategories.map(category =>

                                                        <option name={ category.post_category } value={ category.post_category } key={ category.id }>{ category.post_category }</option>
                                                    ) : null
                                            }
                                        </select>
                                        { formErrors.post_categoryError &&
                                            <div class="input-errorMsg">{ formErrors.post_categoryError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="post_subcategory" onChange={ this.handleChange } value={ this.post_subcategory } placeholder="Blog Sub Category"/>
                                        { formErrors.post_subcategoryError &&
                                            <div class="input-errorMsg">{ formErrors.post_subcategoryError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="file" onChange={ this.onUpload } name="post_image" value={ this.post_image } accept=".png, .jpg" placeholder="Image"/>
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <textarea rows="1" class="form-control" type="text" name="post_description" onChange={ this.handleChange } value={ this.post_description } placeholder="Blog Description"/>
                                        { formErrors.post_descriptionError &&
                                            <div class="input-errorMsg">{ formErrors.post_descriptionError }</div>
                                        }
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" name="post_date" value={ this.state.post_date } accept=".png, .jpg" disabled/>
                                    </div>
                                </div>
                                <div className="form-box">

                                    <div className="div-input">
                                        <input class="form-control" type="text" name="post_authname" onChange={ this.handleChange } value={ this.post_authname } placeholder="Your Name"/>
                                        { formErrors.post_authnameError &&
                                            <div class="input-errorMsg">{ formErrors.post_authnameError }</div>
                                        }
                                    </div>
                                </div>

                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" onClick={ this.handleSubmit } class="btn-footer">Submit</button>
                            <button type="button" onClick={ this.props.handleClose } className="btn-footer">Cancel</button>
                        </div>
                    </div>
                </div>
                <Message show={ this.state.message } messageHide={ messageHide }>
                    <h3>Success !</h3>
                    <p>{ responseData }</p>
                    <div className="m-footer">
                        <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                    </div>
                </Message>
            </div>
        );

    }
}
