import React, { Component } from 'react'
import '../../Admin-css/Admin-Detail.css'
import Edit from './EditNews'
import Message from './Message'
import Alert from './AlertMessage'

import Axios from '../../../../axios'

export default class ViewNews extends Component {

    constructor(props) {
        super(props)
        let setImage = 0

        this.state = {

            Allnews: [],
            ncategories: [],

            id: '',

            title: '',
            news_category: '',
            news_subcategory: '',
            content: '',
            image: null,
            source: '',

            message: '',

            responseData: '',

            formErrors: {},

            setImage,
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);

        this.closeModal = this.closeModal.bind(this);
        this.openModal = this.openModal.bind(this);

        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);

        this.closeAlertMessage = this.closeAlertMessage.bind(this);

        this.onUpload = this.onUpload.bind(this);
        this.triggerInputFile = this.triggerInputFile.bind(this);
        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    openModal = (e) => {
        this.setState({
            show: true
        })
    }

    closeModal = (e) => {
        this.setState({
            show: false
        })
    }

    openMessage = (e) => {
        this.setState({
            message: true,
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }

    openAlertMessage = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }

    onUpload(e) {
        // let file = e.target.files[0];
        this.setState({
            image: e.target.files[0],
            setImage: 1,
        })
        console.log(e.target.files[0]);
    }

    triggerInputFile() { this.fileInput.click() }

    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { title, news_category, content } = this.state

        if (title === '') {
            formErrors.titleError = "News Title is Required!!";
            isValid = false;
        }
        if (news_category === '') {
            formErrors.news_categoryError = "News Category is Required!!";
            isValid = false;
        }

        if (content === '') {
            formErrors.contentError = "Description is Required!!";
            isValid = false;
        }


        if (title.length === 1) {
            formErrors.titleError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (title.length === 2) {
            formErrors.titleError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (title.length === 3) {
            formErrors.titleError = "Title must be of of 4 or more";
            isValid = false;
        }
        if (title.length > 250) {
            formErrors.titleError = "Title must be of length 250";
            isValid = false;
        }


        if (content.length > 2000) {
            formErrors.contentError = "Description must be of length 2000";
            isValid = false;
        }
        if (content.length < 10) {
            formErrors.contentError = "Description must be of 10 or more";
            isValid = false;
        }


        this.setState({ formErrors });
        return isValid;
    }

    componentDidMount() {

        Axios.get('/getNews')
            .then(response => {
                // console.log(response)
                this.setState({ Allnews: response.data })
            })
            .catch(error => {
                console.log(error)
            })

        Axios.get('/getNewsCategory')
            .then(response => {
                // console.log(response)
                this.setState({ ncategories: response.data })
            })
            .catch(error => {
                console.log(error)
            })
    }

    deleteNews(id) {

        Axios.delete(`/deleteNews/${ id }`)
            .then(response => {

                this.setState({
                    alert: false,
                    responseData: response.data.message
                })

                this.openMessage();
            })
            .catch(error => {
                console.log(error);

            })
    }

    editNews = async (id) => {
        console.log('---edit News---')

        let editingItem = this.state.Allnews.find(news => {
            return news.news_id === id;
        })
        // console.log(id);

        await this.setState({
            category_name: editingItem.category_name,
            title: editingItem.title,
            news_category: editingItem.news_category,
            news_subcategory: editingItem.news_subcategory,
            content: editingItem.content,
            image: editingItem.image.split("/").pop(),
            source: editingItem.source,
            editMode: id
        })

        this.openModal();
    }


    handleUpdate(e) {
        e.preventDefault();

        const isValid = this.handleFormValidation();
        if (isValid) {

            let id = this.state.editMode;

            this.setState({
                editMode: true,

            })

            if (this.state.setImage === 1) {

                let data = new FormData();
                data.append('title', this.state.title);
                data.append('news_category', this.state.news_category);
                data.append('news_subcategory', this.state.news_subcategory);
                data.append('content', this.state.content);
                data.append('image', this.state.image);
                data.append('source', this.state.source);

                Axios.put(`/editNews/${ id }/${ this.state.setImage}`, data)

                    .then(response => {

                        this.closeModal();

                        this.setState({
                            responseData: response.data.message
                        })

                        this.openMessage();
                    })

                    .catch(error => {
                        console.log(error);

                    })
            }
            else if (this.state.setImage === 0) {

                const data = {
                    title: this.state.title,
                    news_category: this.state.news_category,
                    news_subcategory: this.state.news_subcategory,
                    content: this.state.content,
                    image: this.state.image,
                    source: this.state.source,

                }

                Axios.post(`/editNews/${ id }`, data)
                    .then(response => {

                        this.closeModal();

                        this.setState({
                            responseData: response.data.message
                        })

                        this.openMessage();
                    })

                    .catch(error => {
                        console.log(error);

                    })
            }
        }
    }



    render() {

        const { formErrors, Allnews, ncategories, responseData } = this.state

        const showHide = this.state.show ? "edit-form display-block" : "edit-form display-none";
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        return (
            <>
                <div className="class-table">
                    <div className="section-title">
                        <h5>News List</h5>
                    </div>

                    <table>
                        <thead>
                            <tr>

                                <th>Image</th>
                                <th>HeadLines</th>
                                <th>Category</th>
                                <th>Content</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>

                            { Allnews.length ?
                                Allnews.map(news =>

                                    <tr key={ news.id }>

                                        <td><img src={ news.image.replace('/public', '')} alt="" /></td>
                                        <td>{ news.title }</td>
                                        <td>{ news.news_category }</td>
                                        <td><div className="some-lines">
                                                { news.content }
                                            </div>
                                        </td>
                                        <td>
                                            <div class="action-btns">
                                                <button class="div-action-btn" onClick={ this.editNews.bind(this, news.news_id) }><i class="fas fa-pen"></i></button>
                                                <button class="div-action-btn" onClick={ this.openAlertMessage.bind(this, news.news_id) } ><i class="fas fa-trash"></i></button>
                                            </div>
                                        </td>
                                    </tr>
                                ) : null }
                        </tbody>
                    </table>


                    <Edit show={ this.state.show } handleClose={ this.closeModal } handleSubmit={ this.handleUpdate } showHide={ showHide }>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="text" name="title" onChange={ this.handleChange } value={ this.state.title } placeholder="Title" />
                                { formErrors.titleError &&
                                    <div class="input-errorMsg">{ formErrors.titleError }</div>
                                }
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <select class="form-control" onChange={ this.handleChange } name="news_category" value={ this.state.news_category } placeholder="Category">
                                    <option>Select Category</option>{
                                        ncategories.length ?
                                            ncategories.map(category =>

                                                <option name={ category.news_category } value={ category.news_category } key={ category.nc_id }>{ category.news_category }</option>
                                            ) : null
                                    }
                                </select>
                                { formErrors.news_categoryError &&
                                    <div class="input-errorMsg">{ formErrors.news_categoryError }</div>
                                }
                            </div>
                        </div>

                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="text" name="news_subcategory" onChange={ this.handleChange } value={ this.state.news_subcategory } placeholder="Sub Category"/>
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" type="text" name="source" onChange={ this.handleChange } value={ this.state.source } placeholder="Source Link" />
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <textarea rows="1" class="form-control" type="text" name="content" onChange={ this.handleChange } value={ this.state.content } placeholder="Description" />
                                { formErrors.contentError &&
                                    <div class="input-errorMsg">{ formErrors.contentError }</div>
                                }
                            </div>
                        </div>
                        <div className="form-box">

                            <div className="div-input">
                                <input class="form-control" value={ this.state.image } onClick={ this.triggerInputFile } placeholder="Image"/>
                                <input ref={ fileInput => this.fileInput = fileInput } Style={ "display : none" } type="file" onChange={ this.onUpload } name="image" accept=".png, .jpg" />


                            </div>
                        </div>
                    </Edit>

                    <Message show={ this.state.message } messageHide={ messageHide }>
                        <h3>Success !</h3>
                        <p>{ responseData }</p>
                        <div className="m-footer">
                            <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                        </div>
                    </Message>

                    <Alert show={ this.state.alert } alertHide={ alertHide }>
                        <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                        <div className="m-footer">

                            <button type="button" onClick={ this.deleteNews.bind(this, this.state.id) } className="btn-footer">Ok</button>
                            <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                        </div>
                    </Alert>
                </div>
            </>
        )
    }
}
