import React, { Component } from 'react'
import '../../Admin-css/Admin.css'
import Axios from '../../../../axios'

import Message from './Message'
import Alert from './AlertMessage'
import Edit from './Edit'

export default class Category extends Component {

    constructor (props) {
        super(props)

        this.state = {
            ncategories: [],
            news_category: '',
            nc_id: '',

            responseData: '',

            show: false,
            message: false,
            alert: false,
            editMode: false,
            formErrors: {},
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.openModal = this.openModal.bind(this);
        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);
        this.closeAlertMessage = this.closeAlertMessage.bind(this);
        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    componentDidMount() {

        Axios.get('/getNewsCategory')
            .then(response => {
                // console.log(response)
                this.setState({ ncategories: response.data })
            })
            .catch(error => {
                console.log(error)
            })
    }


    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    refreshPage() {
        window.location.reload(false);
    }

    openModal = (e) => {
        this.setState({
            show: true
        })
    }

    closeModal = (e) => {
        this.setState({
            show: false
        })
    }

    openMessage = (e) => {
        this.setState({
            message: true
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,
        })
        this.refreshPage();
    }


    openAlert = (id) => {
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage = (e) => {
        this.setState({
            alert: false,

        })
    }

    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { news_category } = this.state;

        if (news_category === '') {
            formErrors.news_categoryError = "Category Name is Required!!";
            isValid = false;
        }

        if (news_category.length === 1) {
            formErrors.news_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (news_category.length === 2) {
            formErrors.news_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (news_category.length === 3) {
            formErrors.news_categoryError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (news_category.length > 50) {
            formErrors.news_categoryError = "Category Name must be of length 50";
            isValid = false;
        }
        if (news_category.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.news_categoryError = "No Special Characters";
            isValid = false;
        }

        this.setState({ formErrors });
        return isValid;

    }


    addCategory = (e) => {
        e.preventDefault();

        const isValid = this.handleFormValidation();

        if (isValid) {

            const data ={
                news_category: this.state.news_category
            }
            Axios.post('/addNewsCategory', data)
                .then(response => {
                    // console.log(response);
                    this.setState({
                        responseData: response.data.message
                    })

                    this.openMessage();

                })

                .catch(error => {
                    console.log(error);
                })
        }

    }

    deleteCategory(id) {

        Axios.delete(`/deleteNewsCategory/${ id }`)
            .then(response => {
                // console.log(response);
                this.setState({
                    alert: false,
                    responseData: response.data.message

                })
                this.openMessage();
            })
            .catch(error => {
                console.log(error);

                // console.log("error-----")
            })
    }

    editCategory = async (id) => {


        let editingItem = this.state.ncategories.find(category => {
            return category.nc_id === id;
        })
        // console.log(id);

        await this.setState({
            news_category: editingItem.news_category,
            editMode: id
        })

        this.openModal();
    }



    handleUpdate(e) {
        e.preventDefault();

        let id = this.state.editMode;
        console.log('...handle edit....')
        this.setState({
            editMode: true,

        })
        const data = {
            news_category: this.state.news_category
        }
        Axios.put(`/editNewsCategory/${ id }`, data)
            .then(response => {
                // console.log(response);
                this.closeModal();
                this.setState({
                    responseData: response.data.message
                })
                this.openMessage();

            })

            .catch(error => {
                console.log(error);

            })
    }

    render() {

        const showHide = this.state.show ? "modal-wrapper display-block" : "modal-wrapper display-none";
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        const { formErrors, ncategories, responseData } = this.state

        return (
            <>
                <div className="dash-class">
                    <div className="class-title">
                        <h3><a href="/admin_dashboard/news-category">News Category</a></h3>
                    </div>

                    <div className="section div-section">
                        <div className="div-class class-width">
                            <div className="section-title">
                                <h5>Create News Category</h5>
                            </div><br />
                            <form onSubmit={ this.addCategory }>

                                <div className="div-input">
                                    <input className="form-control" type="text" name="news_category" placeholder="Category Name" onChange={ this.handleChange } value={ this.news_category } />
                                    { formErrors.news_categoryError &&
                                        <div class="input-errorMsg">{ formErrors.news_categoryError }</div>
                                    }
                                </div>

                                <div className="div-button">
                                    <button type="submit" class="add-btn">Create</button>
                                </div>

                            </form>
                        </div>

                        <div className="div-class">
                            <div className="class-table">
                                <div className="section-title">
                                    <h5>News Category List</h5>
                                </div>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Id</th>
                                            <th>Category</th>
                                            <th>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {
                                            ncategories.length ?
                                                ncategories.map(category => <tr key={ category.id }>
                                                    <td>{ category.nc_id }</td>
                                                    <td>{ category.news_category }</td>

                                                    <td class="action-btns"><button class="div-action-btn" onClick={ this.editCategory.bind(this, category.nc_id) }><i class="fas fa-pen"></i></button>
                                                        <button class="div-action-btn" onClick={ this.openAlert.bind(this, category.nc_id) }><i class="fas fa-trash"></i></button></td>
                                                </tr>) : null
                                        }

                                    </tbody>
                                </table>

                            </div>

                        </div>

                        <Message show={ this.state.message } messageHide={ messageHide }>
                            <h3>Success !</h3>
                            <p>{responseData}</p>
                            <div className="m-footer">
                                <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                            </div>
                        </Message>

                        <Alert show={ this.state.alert } alertHide={ alertHide }>
                            <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                            <div className="m-footer">

                                <button type="button" onClick={ this.deleteCategory.bind(this, this.state.id) } className="btn-footer">Ok</button>
                                <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                            </div>
                        </Alert>

                        <Edit show={ this.state.show } handleClose={ this.closeModal } handleSubmit={ this.handleUpdate } showHide={ showHide }>
                            <div className="container">
                                <div>
                                    <div className="div-label">
                                        <label>Category Name</label>
                                    </div>
                                    <div className="div-input">
                                        <input className="form-control" type="text" name="news_category" onChange={ this.handleChange } value={ this.state.news_category } required />
                                    </div>
                                </div>
                            </div>

                        </Edit>
                    </div>

                </div>
            </>
        )
    }
}
