import React, { Component } from 'react'
import '../../Admin-css/Admin-Detail.css'

import Add from './AddNews'
import View from './ViewNews'

export default class News extends Component {

    constructor (props) {
        super(props)

        this.state = {
            form: false,
        }

        this.addForm = this.addForm.bind(this);
        this.closeForm = this.closeForm.bind(this);
    }

    addForm = (e) => {
        this.setState({
            form: true

        })
    }

    closeForm = (e) => {

        this.setState({
            form: false
        })

    }

    render() {

        const showHide = this.state.form ? "form display-block" : "form display-none";

        return (
            <div>
                <div className="dash-detailClass">
                    <div className="class-title">
                        <h3><a href="/admin_dashboard/news-content">News</a></h3>
                        <div className="div-row-btn">
                            <button className="add-btn" onClick={ this.addForm }>Add</button>
                        </div>
                    </div>

                    <div className="section">
                        <div className="div-class">
                            <View />
                            <Add show={ this.state.form } handleClose={ this.closeForm } handleSubmit={ this.handleSubmit } showHide={ showHide } />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
