import React, { Component } from 'react'
import '../../Admin-css/Admin.css'
import Axios from '../../../../axios'

import Message from './Message'
import Alert from './AlertMessage'
import Edit from './Edit'

export default class Category extends Component {

    constructor(props) {
        super(props)

        this.state = {
            categories: [],
            category_name : '',
            id: '',

            responseData: '',

            show: false,
            message: false,
            alert: false,
            editMode: false,
            formErrors: {},
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.openModal = this.openModal.bind(this);
        this.openMessage = this.openMessage.bind(this);
        this.closeMessage = this.closeMessage.bind(this);
        this.closeAlertMessage = this.closeAlertMessage.bind(this);
        this.refreshPage = this.refreshPage.bind(this);
        this.handleFormValidation = this.handleFormValidation.bind(this);
    }

    componentDidMount(){

        Axios.get('/getCategory')
        .then( response =>{
            // console.log(response)
            this.setState({categories: response.data})
        })
        .catch(error =>{
            console.log(error)
        })
    }


    handleChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    refreshPage(){
        window.location.reload(false);
    }

    openModal = (e) => {
        this.setState({
            show: true
        })
    }

    closeModal = (e) => {
        this.setState({
            show: false
        })
    }

    openMessage = (e) => {
        this.setState({
            message: true
        })
    }

    closeMessage = (e) => {
        this.setState({
            message: false,  
        })
        this.refreshPage();
    }


    openAlert = (id) =>{
        this.setState({
            alert: true,
            id: id,
        })
    }

    closeAlertMessage =(e) =>{
        this.setState({
            alert: false,

        })
    }

    handleFormValidation() {
        const formErrors = {};
        let isValid = true;

        const { category_name } = this.state;

        if (category_name === '') {
            formErrors.category_nameError = "Category Name is Required!!";
            isValid = false;
        }

        if (category_name.length === 1) {
            formErrors.category_nameError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (category_name.length === 2) {
            formErrors.category_nameError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (category_name.length === 3) {
            formErrors.category_nameError = "Category Name must be of of 4 or more";
            isValid = false;
        }
        if (category_name.length > 50) {
            formErrors.category_nameError = "Category Name must be of length 50";
            isValid = false;
        }
        if (category_name.match(/[!@/$%^&*(),.?":{}|<>]/)) {
            formErrors.category_nameError = "No Special Characters";
            isValid = false;
        }

        this.setState({ formErrors });
        return isValid;

    }


    addCategory =(e) =>{
        e.preventDefault();

        const isValid = this.handleFormValidation();

        if (isValid) {


            Axios.post('/addCategory', this.state)
            .then(response =>{
                // console.log(response);
                this.setState({
                    responseData: response.data.message
                })
                this.openMessage();
            })

            .catch(error =>{
                console.log(error);
            })
        }

    }

    deleteCategory(id){

        Axios.delete(`/deleteCategory/${id}`)
        .then(response =>{
            // console.log(response);
            this.setState({
                alert:false,
                responseData: response.data.message

            })
            this.openMessage();
        })
        .catch(error =>{
            console.log(error);

            // console.log("error-----")
        })
    }

    editCategory = async (id) =>{


        let editingItem = this.state.categories.find(category => {
            return category.category_id === id;
        })
        // console.log(id);

       await this.setState({
            category_name: editingItem.category_name,
            editMode: id
        })

        this.openModal();
    }



    handleUpdate(e){
        e.preventDefault();

        let id = this.state.editMode;
        console.log('...handle edit....')
        this.setState({
            editMode: true,

        })

        Axios.put(`/editCategory/${id}`, this.state)
        .then(response =>{
            // console.log(response);
            this.closeModal();
            this.setState({
                responseData: response.data.message
            })
            this.openMessage();

        })

        .catch(error =>{
            console.log(error);

        })
    }

    render() {

        const showHide = this.state.show ? "modal-wrapper display-block" : "modal-wrapper display-none";
        const messageHide = this.state.message ? "message-box display-block" : "message-box display-none";
        const alertHide = this.state.alert ? "message-box display-block" : "message-box display-none";

        const { formErrors, categories, responseData} = this.state

        return (
            <>
                <div className="dash-class">
                    <div className="class-title">
                        <h3><a href="/admin_dashboard/category">Shop Category</a></h3>
                    </div>

                    <div className="section div-section">
                        <div className="div-class class-width">
                            <div className="section-title">
                                <h5>Create Shop Category</h5>
                            </div><br/>
                            <form onSubmit={this.addCategory}>

                                <div className="div-input">
                                    <input className="form-control" type="text" name="category_name" placeholder="Category Name" onChange={this.handleChange} value={this.category_name}/>
                                    { formErrors.category_nameError &&
                                        <div class="input-errorMsg">{ formErrors.category_nameError }</div>
                                    }
                                </div>

                                <div className="div-button">
                                    <button type="submit" class="add-btn">Create</button>
                                </div>

                            </form>
                        </div>

                        <div className="div-class">
                            <div className="class-table">
                                <div className="section-title">
                                    <h5>Shop Category List</h5>
                                </div>
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Id</th>
                                            <th>Category</th>
                                            <th>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {
                                            categories.length ?
                                                categories.map(category => <tr key={ category.id }>
                                                    <td>{ category.category_id }</td>
                                                    <td>{ category.category_name }</td>
                                                    {/* <td><div className="div-action">
                                                            <i class="fas fa-plus"></i>
                                                        </div>
                                                    </td> */}
                                                    <td class="action-btns"><button class="div-action-btn" onClick={ this.editCategory.bind(this, category.category_id) }><i class="fas fa-pen"></i></button>
                                                        <button class="div-action-btn" onClick={ this.openAlert.bind(this, category.category_id) }><i class="fas fa-trash"></i></button></td>
                                                </tr>) : null
                                        }

                                    </tbody>
                                </table>

                            </div>

                        </div>

                        <Message show={ this.state.message } messageHide={ messageHide }>
                            <h3>Success !</h3>
                            <p>{ responseData }</p>
                            <div className="m-footer">
                                <button type="button" onClick={ this.closeMessage } className="btn-footer">Ok</button>
                            </div>
                        </Message>

                        <Alert show={ this.state.alert } alertHide={ alertHide }>
                            <h3>Are You Sure<br /><span>You want to Delete this?</span></h3>
                            <div className="m-footer">

                                <button type="button" onClick={ this.deleteCategory.bind(this,this.state.id) } className="btn-footer">Ok</button>
                                <button type="button" onClick={ this.closeAlertMessage } className="btn-footer">Cancel</button>
                            </div>
                        </Alert>

                        <Edit show={ this.state.show } handleClose={ this.closeModal } handleSubmit={ this.handleUpdate } showHide={ showHide }>
                            <div className="container">
                                <div>
                                    <div className="div-label">
                                        <label>Category Name</label>
                                    </div>
                                    <div className="div-input">
                                        <input className="form-control" type="text" name="category_name" onChange={ this.handleChange } value={ this.state.category_name } required />
                                    </div>
                                </div>
                            </div>

                        </Edit>
                    </div>

                </div>
            </>
        )
    }
}
